class ProgressionAssistant{
    constructor(document){
        this.document = document
        this.PatchList=[];
        this.patchInsertIndex=-1;
        this.insertMode = false;
    }

    createPatch(patchVersion, patchName){
        this.PatchList.push(new Patch(patchVersion, patchName));
    }

    beginInsertion(){
        this.insertMode=true;
        if(this.PatchList.length != 0){
            this.patchInsertIndex=0;
        }
    }

    checkInsertPoint(content){
        if(content.patch != this.PatchList[this.patchInsertIndex].version){
            for(var index = 0; index < this.PatchList.length; index++){
                if(content.patch == this.PatchList[index].version){
                    this.patchInsertIndex = index;
                    return;
                }
            }
        }else{
            return;
        }
        throw "[ProgressionAssistant][checkInsertPoint()][Patch could not be found. Insert point unchanged.]"
    }

    insert(content){
        if(this.insertMode!=true){
            console.log("You are not in insert mode. Aborted!")
            return;
        }
        this.checkInsertPoint(content);
        this.PatchList[this.patchInsertIndex].addContent(content)
    }

    setStateToAll(state){
        for(var patchIndex = 0; patchIndex < this.PatchList.length; patchIndex++){
            for(var contentIndex = 0; contentIndex < this.PatchList[patchIndex].content.length; contentIndex++){
                this.PatchList[patchIndex].content[contentIndex].state = state;
            }
        }
    }

    setStateUntilCount(state, contentCount){
        var counter = contentCount
        for(var patchIndex = 0; patchIndex < this.PatchList.length; patchIndex++){
            for(var contentIndex = 0; contentIndex < this.PatchList[patchIndex].content.length && counter > 0; contentIndex++){
                this.PatchList[patchIndex].content[contentIndex].state = state;
                counter--;
            }
        }
    }

    setStateBetween(state, startIndex, endIndex){
        var currentIndex = 0;
        for(var patchIndex = 0; patchIndex < this.PatchList.length; patchIndex++){
            for(var contentIndex = 0; contentIndex < this.PatchList[patchIndex].content.length && currentIndex <= endIndex; contentIndex++){
                if(currentIndex >= startIndex && currentIndex <= endIndex){
                    this.PatchList[patchIndex].content[contentIndex].state = state;
                }
                currentIndex++;
            }
        }
    }

    updateDisplay(){
        var currentPatchHTML = "";
        for(var patchIndex = 0; patchIndex < this.PatchList.length; patchIndex++){
            if(this.PatchList[patchIndex].content.length <= 0){
                continue;
            }
            currentPatchHTML += "<div id=\""+this.PatchList[patchIndex].version+"\" class=\"patchNode\">"
            currentPatchHTML += "<h2>"+this.PatchList[patchIndex].version;
            if(this.PatchList[patchIndex].name != null){
                currentPatchHTML += ": " + this.PatchList[patchIndex].name;
            }
            currentPatchHTML += "</h2>";
            currentPatchHTML += "<div class=\"contentList\">"
            for(var contentIndex = 0; contentIndex < this.PatchList[patchIndex].content.length; contentIndex++){
                currentPatchHTML += "<div class=\"contentNode "
                if(this.PatchList[patchIndex].content[contentIndex].state != "Unlocked"){
                    currentPatchHTML += this.PatchList[patchIndex].content[contentIndex].state.toLowerCase() + "\"";
                    if(this.PatchList[patchIndex].content[contentIndex].state != "Locked"){
                        currentPatchHTML +=  " onclick=getModal("+patchIndex+","+contentIndex+")>"
                    }else{
                        currentPatchHTML += ">"
                    }
                }else{
                    currentPatchHTML += " \" onclick=getModal("+patchIndex+","+contentIndex+")>"
                }
                if(this.PatchList[patchIndex].content[contentIndex].state != "Locked"){
                    currentPatchHTML += "<div id=\"P"+patchIndex+"C"+contentIndex+"\" class=\"contentBanner\"></div>"
                    currentPatchHTML += "<div class=\"contentInfo\"><p><span class=\"infoBanner\">"
                    currentPatchHTML += "<b class=\"contentType\">" + this.PatchList[patchIndex].content[contentIndex].type + "</b>";
                    currentPatchHTML += "<i class=\"patchTag\"> Patch " + this.PatchList[patchIndex].content[contentIndex].patch + "</i></span><br>";
                    currentPatchHTML += this.PatchList[patchIndex].content[contentIndex].title + "<br>";
                    currentPatchHTML += "<i>"+this.PatchList[patchIndex].content[contentIndex].difficulty+" | <b>Lv.</b>" +  this.PatchList[patchIndex].content[contentIndex].level + "</i> | ";
                    if(this.PatchList[patchIndex].content[contentIndex].ilevel == "-"){
                        currentPatchHTML += "<i><b>iLv.</b>" + "---" + "</i></p></div></div>"
                    }else{
                        currentPatchHTML += "<i><b>iLv.</b>" +  this.PatchList[patchIndex].content[contentIndex].ilevel + "</i></p></div></div>"
                    }
                }else{
                    currentPatchHTML += "<div id=\"P"+patchIndex+"C"+contentIndex+"\" class=\"contentBanner\"></div>"
                    currentPatchHTML += "<div class=\"contentInfo\"><p><span class=\"infoBanner\">"
                    currentPatchHTML += "<b class=\"contentType\">" + "???" + "</b>";
                    currentPatchHTML += "<i class=\"patchTag\"> Patch " + this.PatchList[patchIndex].content[contentIndex].patch + "</i></span><br>";
                    currentPatchHTML += "???" + "<br>";
                    currentPatchHTML += "<i>"+"???"+" | <b>Lv.</b>" +  "???" + "</i> | ";
                    currentPatchHTML += "<i><b>iLv.</b>" + "???" + "</i></p></div></div>"
                }
                
            }
            currentPatchHTML += "</div></div>"
        }
        this.document.getElementsByClassName("patchList")[0].innerHTML = currentPatchHTML;
        for(var patchIndex = 0; patchIndex < this.PatchList.length; patchIndex++){
            for(var contentIndex = 0; contentIndex < this.PatchList[patchIndex].content.length; contentIndex++){
                this.document.getElementById("P"+patchIndex+"C"+contentIndex).style.backgroundImage = "url("+this.PatchList[patchIndex].content[contentIndex].imageBanner+")";
            }
        }
    }

    updateModal(content){
        this.document.getElementsByClassName("web-overlay")[0].style.display = "block";
        this.document.getElementById("content-title").innerHTML = content.title;
        this.document.getElementById("content-banner").src = content.imageBanner;
        this.document.getElementById("content-description").innerHTML = content.description;
        this.document.getElementById("content-levels").innerHTML = "<b>Level</b> " + content.level + " <b>| Item Level</b> ";
        if(content.ilevel == "-"){
            this.document.getElementById("content-levels").innerHTML+= "---";
        }else{
            this.document.getElementById("content-levels").innerHTML += content.ilevel;
        }
        if(content.type != "PvP"){
            this.document.getElementById("content-difficulty").innerHTML = "<b>Difficulty:</b> " + content.difficulty;
        }else{
            this.document.getElementById("content-difficulty").innerHTML = "<b>Mode:</b> " + content.difficulty;
        }
        var timeString = content.time;
        timeString = "<b>" + timeString.substring(0, timeString.indexOf(":")+1)+"</b>" + timeString.substring(timeString.indexOf(":")+1, timeString.length);
        this.document.getElementById("content-time").innerHTML = timeString;
        var infoString = content.info;
        if(infoString.search("Tanks") != -1){
            infoString = infoString.replace("Tanks","<img src=\"images/icons/TankRole.png\" style=\"width:24px;height:24px;\" alt=\"Tank Role(s)\">");
        }else if(infoString.search("Tank") != -1){
            infoString = infoString.replace("Tank","<img src=\"images/icons/TankRole.png\" style=\"width:24px;height:24px;\" alt=\"Tank Role(s)\">");
        }
        if(infoString.search("Healers") != -1){
            infoString = infoString.replace("Healers","<img src=\"images/icons/HealerRole.png\" style=\"width:24px;height:24px;\" alt=\"Healer Role(s)\">");
        }else if(infoString.search("Healer") != -1){
            infoString = infoString.replace("Healer","<img src=\"images/icons/HealerRole.png\" style=\"width:24px;height:24px;\" alt=\"Healer Role(s)\">");
        }
        if(infoString.search("DPS") != -1){
            infoString = infoString.replace("DPS","<img src=\"images/icons/DPSRole.png\" style=\"width:24px;height:24px;\" alt=\"DPS Role(s)\">");
        }
        this.document.getElementById("content-players").innerHTML = infoString;
        this.document.getElementsByClassName("button-link")[0].href = content.eorzeaDBLink;
    }

    getModal(patchID, contentID){
        if(patchID >= 0 && patchID < this.PatchList.length){
            if(contentID >= 0 && contentID < this.PatchList[patchID].content.length){
                this.updateModal(this.PatchList[patchID].content[contentID]);  
                return;     
            }
        }
        throw "[ProgressionAssistant][getModal()][Invalid parameter(s) have been provided!]"
    }

    dismissModal(){
        this.document.getElementsByClassName("web-overlay")[0].style.display = "none";
    }
}

